#include <stdio.h>
#include <unistd.h>
#include <sys/wait.h>

int main() {
    int x = 10;
    if (fork() == 0) {
        x += 5;
        if (fork() == 0) {
            printf("%d ", x);
        } else {
            wait(NULL);
            printf("%d ", x);
        }
    } else {
        wait(NULL);
        printf("%d ", x);
    }
    return 0;
}
