#include <stdio.h>
#include <unistd.h>
#include <sys/wait.h>

int main() {
    printf("1\n");
    if (fork() == 0) {
        printf("2\n");
        execlp("/bin/echo", "echo", "3", NULL);
        printf("4\n");
    } else {
        wait(NULL);
        printf("5\n");
    }
    return 0;
}
