#include <stdio.h>
#include <unistd.h>
#include <sys/wait.h>

int main() {
    int x = 10;
    pid_t pid = fork();

    if (pid == 0) {
        // תהליך הבן
        x = x + 5;
        printf("Child: x = %d\n", x);
    } else {
        // תהליך האב
        wait(NULL);
        x = x - 5;
        printf("Parent: x = %d\n", x);
    }
    return 0;
}
