#include <pthread.h>
#include <stdio.h>

void* thread_func(void* arg) {
    int val = *(int*)arg;
    printf("%d ", val);
    return NULL;
}

int main() {
    pthread_t threads[5];
    for (int i = 0; i < 5; i++) {
        pthread_create(&threads[i], NULL, thread_func, &i);
    }
    for (int i = 0; i < 5; i++) {
        pthread_join(threads[i], NULL);
    }
    return 0;
}
