#include <pthread.h>
#include <stdio.h>

int global_sum = 0;

void* thread_func(void* arg) {
    int val = *(int*)arg;
    for (int i = 0; i < 1000; i++) {
        global_sum += val;
    }
    return NULL;
}

int main() {
    pthread_t threads[2];
    for (int i = 1; i <= 2; i++) {
        pthread_create(&threads[i-1], NULL, thread_func, &i);
    }
    for (int i = 0; i < 2; i++) {
        pthread_join(threads[i], NULL);
    }
    printf("%d\n", global_sum);
    return 0;
}
