#include <stdio.h>
#include <pthread.h>

int global_var = 10;

void* thread_func(void* arg) {
    global_var += 5;
    return NULL;
}

int main() {
    pthread_t tid;
    pthread_create(&tid, NULL, thread_func, NULL);
    pthread_join(tid, NULL);
    printf("%d\n", global_var);
    return 0;
}
