#include <stdio.h>
#include <pthread.h>

int x = 10;

void* thread_func(void* arg) {
    x += 5;
    printf("Thread: x = %d\n", x);
    return NULL;
}

int main() {
    pthread_t tid;
    pthread_create(&tid, NULL, thread_func, NULL);
    pthread_join(tid, NULL);
    x += 2;
    printf("Main: x = %d\n", x);
    return 0;
}
