#include <pthread.h>
#include <stdio.h>

int global_var = 20;

void* thread_routine(void* arg) {
    global_var += 30;
    return NULL;
}

int main() {
    pthread_t tid;
    if (pthread_create(&tid, NULL, thread_routine, NULL) != 0) {
        return 1;
    }
    pthread_join(tid, NULL);
    printf("%d", global_var);
    return 0;
}
