#include <pthread.h>
#include <stdio.h>

void* print_id(void* arg) {
    int id = *(int*)arg;
    printf("%d ", id);
    return NULL;
}

int main() {
    pthread_t threads[3];
    for (int i = 0; i < 3; i++) {
        pthread_create(&threads[i], NULL, print_id, &i);
    }
    for (int i = 0; i < 3; i++) {
        pthread_join(threads[i], NULL);
    }
    return 0;
}
