#include <pthread.h>
#include <stdio.h>

int count = 0;

void* increment(void* arg) {
    int limit = *(int*)arg;
    for (int i = 0; i < limit; i++) {
        count++;
    }
    return NULL;
}

int main() {
    pthread_t t1, t2, t3;
    int val1 = 10, val2 = 20, val3 = 30;

    pthread_create(&t1, NULL, increment, &val1);
    pthread_join(t1, NULL);

    pthread_create(&t2, NULL, increment, &val2);
    pthread_create(&t3, NULL, increment, &val3);

    pthread_join(t2, NULL);
    pthread_join(t3, NULL);

    printf("%d", count);
    return 0;
}
