#include <pthread.h>
#include <stdio.h>

int counter = 0;

void* func(void* arg) {
    for (int i = 0; i < 100; i++) {
        int temp = counter;
        counter = temp + 1;
    }
    return NULL;
}

int main() {
    pthread_t t1, t2;
    pthread_create(&t1, NULL, func, NULL);
    pthread_create(&t2, NULL, func, NULL);
    pthread_join(t1, NULL);
    pthread_join(t2, NULL);
    printf("%d\n", counter);
    return 0;
}
