#include <pthread.h>
#include <stdio.h>

int counter = 0;

void* thread_func(void* arg) {
    int val = *((int*)arg);
    for (int i = 0; i < 100; i++) {
        counter++;
    }
    printf("%d ", val);
    return NULL;
}

int main() {
    pthread_t threads[5];
    for (int i = 0; i < 5; i++) {
        pthread_create(&threads[i], NULL, thread_func, &i);
    }
    for (int i = 0; i < 5; i++) {
        pthread_join(threads[i], NULL);
    }
    printf("\nCounter: %d\n", counter);
    return 0;
}
