#include <pthread.h>
#include <stdio.h>

int counter = 0;

void* worker(void* arg) {
    int val = *(int*)arg;
    int local = counter;
    local++;
    counter = local;
    printf("%d", val);
    return NULL;
}

int main() {
    pthread_t t[3];
    for (int i = 0; i < 3; i++) {
        pthread_create(&t[i], NULL, worker, &i);
    }
    for (int i = 0; i < 3; i++) {
        pthread_join(t[i], NULL);
    }
    printf(" Final: %d\n", counter);
    return 0;
}
