#include <stdio.h>
#include <pthread.h>

int counter = 0; // משתנה גלובלי משותף

void* increment_thread(void* arg) {
    for (int i = 0; i < 5; ++i) {
        // פעולה זו אינה אטומית
        counter++; 
    }
    return NULL;
}

int main() {
    pthread_t tid1, tid2;

    // יצירת שני חוטים
    pthread_create(&tid1, NULL, increment_thread, NULL);
    pthread_create(&tid2, NULL, increment_thread, NULL);

    // המתנה לסיום ריצת החוטים
    pthread_join(tid1, NULL);
    pthread_join(tid2, NULL);

    // הדפסת הערך הסופי של counter
    printf("Final counter value: %d\n", counter);

    return 0;
}
