// signal2.c
void signal_handler(int signal) {
    if (signal == SIGCHLD) {
        int rc = wait(NULL);
        printf("child terminated %d (pid:%d)\n", rc, getpid());
    }
}

int main(int argc, char *argv[])
{
    struct sigaction act;
    sigemptyset(&act.sa_mask);
    act.sa_handler = signal_handler;
    act.sa_flags = 0;

    sigaction(SIGCHLD, &act, NULL);
    if (fork()) {
        while (1);
    }
}
